def saveprisoner(mat):
    answerarr = []
    for x in mat:
        n = x[0]
        m = x[1]
        s = x[2]
        if n == m and s > 1:
            answerarr.append(s-1)
        elif n == m and s == 1:
            answerarr.append(m)
        else:
            adder = m % n - 1
            tot1 = s + adder
            if tot1 > n:
                tot1 = tot1-n
            if tot1 == 0:
                answerarr.append(n)
            else:
                answerarr.append(tot1)
    print(f'answerarr: {answerarr}')
    print(f'   answer: {answer2}')
    for y in range(len(answerarr)):
        if answerarr[y] != answer2[y]:
            print(f'answerarr: {answerarr[y]}, answer: {answer2[y]}, array: {mat[y]}')
    print(f'  answer: {answer2}\nanswerarr: {answerarr}')


